h=1/6; x=[h:h:1];
fx=-1/2*x.^2+1/2; plot(x,fx)
A=toeplitz([2 -1 zeros(1,1/h-3)],[2 -1 zeros(1,1/h-3)]);
A(1,2)=-2;
hold on
U=A/(h^2)\ones(1/h-1,1);
U=[U;0];
plot(x,U,'g')
A=toeplitz([2 -1 zeros(1,1/h-3)],[2 -1 zeros(1,1/h-3)]);
A(1,1)=1;
hold on
U=A/(h^2)\ones(1/h-1,1);
U=[U;0];
plot(x,U,'r')

